definition.script:
pipeline {
    agent any

    stages {
        stage('Run the teest using Docker Container/Image') {
            steps {
                echo 'Docker run...'
                sh 'docker run yehory/api_tests_java:2.0'
            }
        }
        stage('Delete Docker Container/Image') {
            steps {
                echo 'Deleting...'
                sh 'docker system prune -af'
            }
        }
    }
}
