definition.script:
pipeline {
    agent { 
        label 'macOS' 
    }

    stages {
        stage('Cloning the Repo') {
            steps {
                echo 'Cloning...'
                git branch: 'main', url: 'https://github.com/YehorYehorychev/Docker-Project-Coffee.git'
            }
        }

        stage('Run the tests using Docker Compose File') {
            steps {
                echo 'Docker compose up...'
                dir('ci_cd') {
                    sh 'docker compose -f docker-compose.tests.yml up --abort-on-container-exit'
                    sh 'docker compose -f docker-compose.tests.yml down || true'
                }
            }
        }

        stage('Delete Docker Container/Image') {
            steps {
                echo 'Deleting...'
                sh 'docker system prune -af'
            }
        }
    }
}
